package com.msb.controller;import javax.servlet.ServletException;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;@WebServlet(urlPatterns ="/Servlet.do")public class Servlet extends HttpServlet {    @Override    protected void service(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        resp.setCharacterEncoding("UTF-8");        resp.setContentType("text/html;charset=UTF-8");        resp.getWriter().write("<h1>Servlet 响应。。。</h1>");    }}